var link=document.querySelector(".writeus-link"),popup=document.querySelector(".modal-feedback"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),nickname=form.querySelector(".field-name"),mail=form.querySelector(".field-mail"),message=form.querySelector(".field-message");link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-show");nickname.focus()});close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show")});
form.addEventListener("submit",function(a){nickname.value&&mail.value&&message.value||(a.preventDefault(),nickname.value||nickname.classList.add("field-invalid"),mail.value||mail.classList.add("field-invalid"),message.value||message.classList.add("field-invalid"));nickname.value&&nickname.classList.remove("field-invalid");mail.value&&mail.classList.remove("field-invalid");message.value&&message.classList.remove("field-invalid")});
window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-show")&&popup.classList.remove("modal-show")});var maplink=document.querySelector(".map"),mappopup=document.querySelector(".modal-map"),mapclose=mappopup.querySelector(".modal-close");maplink.addEventListener("click",function(a){a.preventDefault();mappopup.classList.add("modal-show")});mapclose.addEventListener("click",function(a){a.preventDefault();mappopup.classList.remove("modal-show")});
window.addEventListener("keydown",function(a){27===a.keyCode&&mappopup.classList.contains("modal-show")&&mappopup.classList.remove("modal-show")});